import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/products');
      setProducts(response.data);
      setLoading(false);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={styles.loading}>
        <h2>–ó–∞–≥—Ä—É–∑–∫–∞ —Ç–æ–≤–∞—Ä–æ–≤...</h2>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>üõí9 –ò–Ω—Ç–µ—Ä–Ω–µ—Ç-–º–∞–≥–∞–∑–∏–Ω</h1>

      <div style={styles.grid}>
        {products.map(product => (
          <div key={product.id} style={styles.card}>
            {/* –§–û–¢–û –¢–û–í–ê–†–ê */}
            <img 
              src={product.image || 'https://via.placeholder.com/300x200/1a1f2e/4ade80?text=No+Image'} 
              alt={product.name}
              style={styles.image}
            />

            <div style={styles.content}>
              <h3 style={styles.name}>{product.name}</h3>
              <p style={styles.category}>{product.category}</p>
              <p style={styles.description}>{product.description}</p>

              <div style={styles.row}>
                <span style={styles.price}>{product.price.toLocaleString()} ‚ÇΩ</span>
                <span style={product.stock < 5 ? styles.lowStock : styles.stock}>
                  –í –Ω–∞–ª–∏—á–∏–∏: {product.stock}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
    background: '#0b0f19',
    color: 'white',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif'
  },
  header: {
    textAlign: 'center',
    marginBottom: '30px',
    color: '#4ade80'
  },
  loading: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    background: '#0b0f19',
    color: 'white'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '20px',
    maxWidth: '1200px',
    margin: '0 auto'
  },
  card: {
    border: '1px solid #333',
    borderRadius: '12px',
    overflow: 'hidden',
    background: '#1a1f2e',
    transition: 'transform 0.2s',
    cursor: 'pointer'
  },
  image: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
    borderBottom: '1px solid #333'
  },
  content: {
    padding: '15px'
  },
  name: {
    margin: '0 0 5px 0',
    fontSize: '18px',
    color: 'white'
  },
  category: {
    margin: '0 0 10px 0',
    fontSize: '14px',
    color: '#888'
  },
  description: {
    margin: '0 0 15px 0',
    fontSize: '14px',
    color: '#ccc',
    lineHeight: '1.4'
  },
  row: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginTop: '10px'
  },
  price: {
    color: '#4ade80',
    fontSize: '20px',
    fontWeight: 'bold'
  },
  stock: {
    color: '#888',
    fontSize: '14px'
  },
  lowStock: {
    color: '#f87171',
    fontSize: '14px',
    fontWeight: 'bold'
  }
};

export default App;
